import React, { Component } from "react";

class Task extends Component {
  state = {
    value: "default value"
  };

  onHandleInput = e => {
    this.setState(state => ({ value: state.value + 1 }));
  };

  render() {
    const { value } = this.state;
    return (
      <>
        <p>{value}</p>
        <input type="text" onChange={this.onHandleInput} />
      </>
    );
  }
}

export default Task;
